mapper.update:
update /*+ customer.upd */
hair_customer
set cust_name = #{cust_name}
	, cust_sex = #{cust_sex}
	, cust_phone1 = #{cust_phone1}
	, cust_phone2 = #{cust_phone2}
	, cust_phone3 = #{cust_phone3}
	, cust_etc = #{cust_etc}
	, cust_upd_date = now()
where cust_ymd = #{cust_ymd}
and cust_no = #{cust_no}
and mem_id = #{mem_id}

delete /*+ customer.del */
from hair_customer
where cust_ymd = #{cust_ymd}
and cust_no = #{cust_no}
and mem_id = #{mem_id}
